def synergy_to_hid(id):
    """
    Translate X11 keycodes to HID keycodes.
    """
    if id < 0x100:
        return table[id]
    if id >=0xEF00 and id <= 0xF000:
        return ext_tab[id - 0xEF00]
    if id >=0xE000 and id <= 0xE100:
        return ext_tab[id - 0xE000]
    return 0